function update_title()
	if btn(5) then
		state = "game"
		music(-1, 30)
		init_game()
	end
end

function draw_title()
	cls()
	print(titlescreen)
  if blink_flag then 
		print("PRESS ❎ TO START", 35, 90, 12)
  end
  if (blink_timer % 30 == 0) blink_flag=not blink_flag
  blink_timer+=1
	print("Move: ⬅️ ➡️ ⬆️ ⬇️", 30, 100, 7)
	print("Dash: C", 40, 110, 7)
end
